// frontend-ulsgram/api/api.js
import axios from 'axios';

// ¡USANDO TU IP LOCAL CONFIRMADA!
const BASE_URL = 'http://192.168.1.3:8000/api';

const api = axios.create({
  baseURL: BASE_URL,
  headers: {
    'Content-Type': 'application/json',
    'Accept': 'application/json',
  },
  // Esto es crucial si tu backend de Laravel usa sesiones o cookies para la autenticación
  // (aunque para Sanctum con tokens, a veces no es estrictamente necesario aquí)
  withCredentials: true,
});

export default api;
