import React, { useState } from 'react';
import { View, Text, TextInput, Button, Alert } from 'react-native';
import api from '../../api/api';

export default function LoginScreen() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [token, setToken] = useState(null);

  const handleLogin = async () => {
    try {
      const response = await api.post('/login', { email, password });
      setToken(response.data.access_token);
      Alert.alert('Login Exitoso', 'Token obtenido');

      // Opcional: mostrar datos de usuario
      console.log('Usuario:', response.data.user);
    } catch (error) {
      console.error('Error de login:', error.response?.data || error.message);
      Alert.alert('Error', 'Credenciales incorrectas o problema de conexión');
    }
  };

  const fetchPosts = async () => {
    if (!token) {
      Alert.alert('Error', 'Debes iniciar sesión primero');
      return;
    }

    try {
      const response = await api.get('/posts', {
        headers: { Authorization: `Bearer ${token}` },
      });

      Alert.alert('Posts', JSON.stringify(response.data));
      console.log('Posts:', response.data);
    } catch (error) {
      console.error('Error al obtener posts:', error.response?.data || error.message);
      Alert.alert('Error', 'No se pudieron obtener los posts');
    }
  };

  return (
    <View style={{ padding: 20 }}>
      <Text style={{ fontSize: 20, marginBottom: 10 }}>Login</Text>

      <TextInput
        placeholder="Email"
        value={email}
        onChangeText={setEmail}
        keyboardType="email-address"
        autoCapitalize="none"
        style={{ marginBottom: 10, borderWidth: 1, padding: 8 }}
      />

      <TextInput
        placeholder="Password"
        value={password}
        onChangeText={setPassword}
        secureTextEntry
        style={{ marginBottom: 10, borderWidth: 1, padding: 8 }}
      />

      <Button title="Iniciar sesión" onPress={handleLogin} />

      {token && (
        <>
          <View style={{ height: 20 }} />
          <Button title="Obtener Posts" onPress={fetchPosts} />
        </>
      )}
    </View>
  );
}
