// frontend-ulsgram/screens/Auth/LoginScreen.js
import React from 'react';
import { View, Text, Button, Alert } from 'react-native';
import api from '../../api/api'; // <--- Importa tu instancia de Axios configurada

export default function LoginScreen() {
  const handleLogin = async () => {
    try {
      // **¡USANDO TU INSTANCIA 'api' AQUÍ!**
      const response = await api.post('/login', { // Solo la ruta relativa
        email: 'tucorreo@ejemplo.com',
        password: '12345678',
      });

      Alert.alert('Login Exitoso', `Token: ${response.data.access_token}`);
      console.log('User data:', response.data.user);
    } catch (error) {
      console.error('Error de inicio de sesión:', error.response?.data || error.message);
      if (error.response) {
        Alert.alert('Error del Servidor', JSON.stringify(error.response.data));
      } else if (error.request) {
        Alert.alert('Error de Red', 'No se recibió respuesta del servidor. Verifica la conexión y la URL.');
      } else {
        Alert.alert('Error Desconocido', error.message);
      }
    }
  };

  return (
    <View style={{ padding: 20 }}>
      <Text style={{ fontSize: 20, marginBottom: 10 }}>Login</Text>
      <Button title="Iniciar sesión" onPress={handleLogin} />
    </View>
  );
}
