// frontend-ulsgram/components/Posts.jsx
import React, { useEffect, useState } from 'react';
import axios from 'axios'; // <-- Asegúrate de que no estás usando este axios

export default function Posts() {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        // **¡CÁMBIALO A ESTO!**
        const response = await axios.get('http://192.168.1.3:8000/api/posts'); // O, mejor aún, usa tu 'api' importada
        setPosts(response.data);
      } catch (error) {
        console.error('Error fetching posts:', error);
      }
    };
    fetchPosts();
  }, []);

  // ... renderizado de posts
}
